import React from "react";
import { FormLabel, makeStyles, TextField } from "@material-ui/core";
import { formDataType, ErrorType } from "../Homepage";

type DemoFormProps = {
  data: formDataType;
  setData: React.Dispatch<React.SetStateAction<formDataType>>;
  errors: ErrorType;
  setErrors: React.Dispatch<React.SetStateAction<ErrorType>>;
};

const useStyles = makeStyles({
  form: {
    backgroundColor: "skyblue",
    margin: "0px 10px",
  },
  formGroup: {
    display: "grid",
    gridTemplateColumns: "repeat(8, 1fr)",
    padding: "5px",
  },
  FormLeftLevel: {
    gridColumn: "1/2",
    color: "black",
  },
  FormRightLevel: {
    gridColumn: "6/7",
    color: "black",
  },
  inputField: {
    border: "1px solid black",
    backgroundColor: "white",
    borderRadius: "50px",
    gridColumn: "2/5",
  },
  inputFieldLarge: {
    border: "1px solid black",
    backgroundColor: "white",
    borderRadius: "50px",
    gridColumn: "2/6",
  },
  inputFieldRight: {
    border: "1px solid black",
    backgroundColor: "white",
    borderRadius: "50px",
    gridColumn: "7/9",
  },
  errorMessage: {
    Color: "red",
    padding: "10px",
  },
});

const ContactInfoAdd: React.FC<DemoFormProps> = (props) => {
  const classes = useStyles();

  // ============================== Methods =========================

  /** 
       This Method for get all change on the Input Field
      */
  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = event.target;

    props.setData((prev) => {
      return {
        ...prev,
        [name]: value,
      };
    });

    props.setErrors((prev) => {
      return {
        ...prev,
        [name]: value,
      };
    });
  };

  /** 
       This Method for validation on the Input Field
      */
  const isValid = () => {
    let hasError = false;
    const copyErrors: ErrorType = { ...props.errors };

    const validationFields = ["address1"];

    for (let key in copyErrors) {
      if (
        validationFields.includes(key) &&
        props.data[key as keyof typeof props.data] === ""
      ) {
        copyErrors[key] = "required";
        hasError = true;
      } else {
        copyErrors[key] = ``;
      }
    }

    if (props.data.address1.length < 5) {
      copyErrors.address1 = "Address Must contain at least 5 characters";
      hasError = true;
    } else {
      copyErrors.address1 = "";
    }

    props.setErrors(copyErrors);

    return hasError;
  };

  return (
    <React.Fragment>
      <form className={classes.form}>
        <div className={classes.formGroup}>
            <FormLabel className={classes.FormLeftLevel}>
              Address line 1
            </FormLabel>
            <TextField
              id="address1"
              name="address1"
              value={props.data.address1}
              required={true}
              onChange={handleChange}
              className={classes.inputField}
              InputProps={{ disableUnderline: true }}
              helperText={props.errors.address1}
              error={Boolean(props.errors.address1)}
            />
            <FormLabel className={classes.FormRightLevel}>group code</FormLabel>
            <TextField
              id="groupCode"
              name="groupCode"
              onChange={handleChange}
              className={classes.inputFieldRight}
              InputProps={{ disableUnderline: true }}
            />
        </div>

        <div className={classes.formGroup}>
          <FormLabel className={classes.FormLeftLevel}>Address 2</FormLabel>
          <TextField
            id="address2"
            name="address2"
            value={props.data.address2}
            InputProps={{ disableUnderline: true }}
            className={classes.inputField}
            onChange={handleChange}
          />
        </div>

        <div className={classes.formGroup}>
          <FormLabel className={classes.FormLeftLevel}>Address 3</FormLabel>
          <TextField
            id="address3"
            name="address3"
            InputProps={{ disableUnderline: true }}
            value={props.data.address3}
            className={classes.inputField}
            onChange={handleChange}
          />
        </div>

        <div className={classes.formGroup}>
          <FormLabel className={classes.FormLeftLevel}>Address 4</FormLabel>
          <TextField
            id="address4"
            name="address4"
            InputProps={{ disableUnderline: true }}
            value={props.data.address4}
            className={classes.inputField}
            onChange={handleChange}
          />
        </div>

        <div className={classes.formGroup}>
          <FormLabel className={classes.FormLeftLevel}>Company Name</FormLabel>
          <TextField
            id="companyName"
            name="companyName"
            InputProps={{ disableUnderline: true }}
            className={classes.inputFieldLarge}
            onChange={handleChange}
          />
        </div>

        <div className={classes.formGroup}>
          <FormLabel className={classes.FormLeftLevel}>
            Department name
          </FormLabel>
          <TextField
            id="departmentName"
            name="departmentName"
            InputProps={{ disableUnderline: true }}
            className={classes.inputFieldLarge}
            onChange={handleChange}
          />
        </div>
      </form>
    </React.Fragment>
  );
};

export default ContactInfoAdd;
