import React from "react";
import { makeStyles, TextField } from "@material-ui/core";
import { formDataType, ErrorType } from "../Homepage";

type DemoFormProps = {
  data: formDataType;
  setData: React.Dispatch<React.SetStateAction<formDataType>>;
  errors: ErrorType;
  setErrors: React.Dispatch<React.SetStateAction<ErrorType>>;
};

const useStyles = makeStyles({
  form: {
    backgroundColor: "skyblue",
    margin: "0px 10px",
  },
  // formGroup: {
  //   gap: "20px",
  //   display: "grid",
  //   gridTemplateColumns: "0.25fr 2fr",
  //   padding: "10px",
  //   // width: "200px",
  // },
  formGroup: {
    display: "grid",
    gridTemplateColumns: "repeat(8, 1fr)",
    padding: "5px",
  },
  inputField: {
    border: "1px solid black",
    backgroundColor: "white",
    borderRadius: "50px",
    gridColumn: "2/5",
  },
  inputFieldRight: {
    border: "1px solid black",
    backgroundColor: "white",
    borderRadius: "50px",
    gridColumn: "7/9",
  },

  formGroupAddress1: {
    gap: "20px",
    display: "grid",
    gridTemplateColumns: "0.5fr 2fr 0.5fr 1.5fr",
    padding: "10px",
  },
  // inputField: {
  //   border: "1px solid black",
  //   backgroundColor: "white",
  //   borderRadius: "50px",
  // },
  addressField: {
    border: "1px solid black",
    backgroundColor: "white",
    borderRadius: "50px",
    width: "250px",
  },
  companyField: {
    border: "1px solid black",
    backgroundColor: "white",
    borderRadius: "50px",
    width: "350px",
  },
  errorMessage: {
    Color: "red",
    padding: "10px",
  },
});

const ContactInfoAdd: React.FC<DemoFormProps> = (props) => {
  const classes = useStyles();

  // ============================== Methods =========================

  /** 
       This Method for get all change on the Input Field
      */
  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = event.target;

    props.setData((prev) => {
      return {
        ...prev,
        [name]: value,
      };
    });

    props.setErrors((prev) => {
      return {
        ...prev,
        [name]: value,
      };
    });
  };

  /** 
       This Method for validation on the Input Field
      */
  const isValid = () => {
    let hasError = false;
    const copyErrors: ErrorType = { ...props.errors };

    const validationFields = [
      "address1",
    ];

    for (let key in copyErrors) {
      if (
        validationFields.includes(key) &&
        props.data[key as keyof typeof props.data] === ""
      ) {
        copyErrors[key] = "required";
        hasError = true;
      } else {
        copyErrors[key] = ``;
      }
    }

    if (props.data.address1.length < 5) {
      copyErrors.address1 = "Address Must contain at least 5 characters";
      hasError = true;
    } else {
      copyErrors.address1 = "";
    }

    props.setErrors(copyErrors);

    return hasError;
  };

  return (
    <React.Fragment>
      <form className={classes.form}>
        <div className={classes.formGroupAddress1}>
          <>
            <label>Address Line 1</label>
            <TextField
              id="address1"
              name="address1"
              value={props.data.address1}
              required={true}
              onChange={handleChange}
              className={classes.addressField}
              InputProps={{ disableUnderline: true }}
              helperText={props.errors.address1}
              error={Boolean(props.errors.address1)}
            />
          </>
          <>
            <label>group code</label>
            <TextField
              id="groupCode"
              name="groupCode"
              onChange={handleChange}
              className={classes.inputField}
              InputProps={{ disableUnderline: true }}
            />
          </>
        </div>

        <div className={classes.formGroup}>
          <label>Address 2</label>
          <TextField
            id="address2"
            name="address2"
            value={props.data.address2}
            InputProps={{ disableUnderline: true }}
            className={classes.addressField}
            onChange={handleChange}
          />
        </div>

        <div className={classes.formGroup}>
          <label>Address 3</label>
          <TextField
            id="address3"
            name="address3"
            InputProps={{ disableUnderline: true }}
            value={props.data.address3}
            className={classes.addressField}
            onChange={handleChange}
          />
        </div>

        <div className={classes.formGroup}>
          <label>Address 4</label>
          <TextField
            id="address4"
            name="address4"
            InputProps={{ disableUnderline: true }}
            value={props.data.address4}
            className={classes.addressField}
            onChange={handleChange}
          />
        </div>

        <div className={classes.formGroup}>
          <label>Company Name</label>
          <TextField
            id="companyName"
            name="companyName"
            InputProps={{ disableUnderline: true }}
            className={classes.companyField}
            onChange={handleChange}
          />
        </div>

        <div className={classes.formGroup}>
          <label>Department Name</label>
            <TextField
              id="departmentName"
              name="departmentName"
              InputProps={{ disableUnderline: true }}
              className={classes.companyField}
              onChange={handleChange}
            />
        </div>
      </form>
    </React.Fragment>
  );
};

export default ContactInfoAdd;
