import React from "react";
import { FormLabel, makeStyles, TextField } from "@material-ui/core";
import { formDataType, ErrorType } from "../Homepage";
import SearchIcon from "@mui/icons-material/Search";

type DemoFormProps = {
  data: formDataType;
  setData: React.Dispatch<React.SetStateAction<formDataType>>;
  errors: ErrorType;
  setErrors: React.Dispatch<React.SetStateAction<ErrorType>>;
};

const useStyles = makeStyles({
  form: {
    backgroundColor: "skyblue",
    margin: "0px 10px",
  },
  formGroup: {
    display: "grid",
    gridTemplateColumns: "repeat(8, 1fr)",
    padding: "5px",
  },
  inputField: {
    border: "1px solid black",
    backgroundColor: "white",
    borderRadius: "50px",
    gridColumn: "2/4",
  },
  inputFieldPrefectures: {
    border: "1px solid black",
    backgroundColor: "white",
    borderRadius: "50px",
    gridColumn: "2/3",
  },
  inputFieldRight: {
    border: "1px solid black",
    backgroundColor: "white",
    borderRadius: "50px",
    gridColumn: "7/9",
  },
  FormLeftLevel: {
    gridColumn: "1/2",
    color: "black",
  },
  FormTitleLevel: {
    gridColumn: "4/5",
    color: "black",
    paddingTop: "10px",
  },
  FormRightLevel: {
    gridColumn: "6/7",
    color: "black",
  },
  titleField: {
    border: "1px solid black",
    backgroundColor: "white",
    borderRadius: "50px",
    gridColumn: "5/6",
  },
  errorMessage: {
    Color: "red",
    padding: "10px",
  },
});

const BasicInfoAdd: React.FC<DemoFormProps> = (props) => {
  const classes = useStyles();
  const phoneNoRegex = "^[0-9-]+$|^$";

  // ============================== Methods =========================

  /** 
       This Method for get all change on the Input Field
      */
  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = event.target;

    props.setData((prev) => {
      return {
        ...prev,
        [name]: value,
      };
    });

    props.setErrors((prev) => ({
      ...prev,
      [name]: "",
    }));
  };

  return (
    <React.Fragment>
      <form className={classes.form}>
          <div className={classes.formGroup}>
            <FormLabel className={classes.FormLeftLevel}>
              phone number
            </FormLabel>
            <TextField
              name="phoneNo"
              value={props.data.phoneNo}
              required={true}
              InputProps={{ disableUnderline: true }}
              className={classes.inputField}
              helperText={props.errors.phoneNo}
              error={Boolean(props.errors.phoneNo)}
              onChange={(event: React.ChangeEvent<HTMLInputElement>) => {
                if (event.target.value.match(phoneNoRegex)) {
                  handleChange(event);
                }
              }}
            />
          </div>

          <div className={classes.formGroup}>
            <FormLabel className={classes.FormLeftLevel}>Your name</FormLabel>
            <TextField
              id="firstName"
              name="firstName"
              className={classes.inputField}
              InputProps={{ disableUnderline: true }}
              value={props.data.firstName}
              required={true}
              onChange={handleChange}
              helperText={props.errors.firstName}
              error={Boolean(props.errors.firstName)}
            />
            <FormLabel className={classes.FormTitleLevel}>title</FormLabel>
            <TextField
              name="lastName"
              value={props.data.lastName}
              InputProps={{ disableUnderline: true }}
              required={true}
              onChange={handleChange}
              className={classes.titleField}
              helperText={props.errors.lastName}
              error={Boolean(props.errors.lastName)}
            />
          </div>

          <div className={classes.formGroup}>
            <FormLabel className={classes.FormLeftLevel}>Furigana</FormLabel>
            <TextField
              name="company"
              value={props.data.company}
              InputProps={{ disableUnderline: true }}
              onChange={handleChange}
              className={classes.inputField}
              helperText={props.errors.company}
              error={Boolean(props.errors.company)}
            />
          </div>
          <div className={classes.formGroup}>
            <FormLabel className={classes.FormLeftLevel}>Search</FormLabel>
            <TextField
              name="search"
              InputProps={{ disableUnderline: true }}
              onChange={handleChange}
              className={classes.inputField}
            />
            <SearchIcon />
            <FormLabel className={classes.FormRightLevel}>sort code</FormLabel>
            <TextField
              name="sortCode"
              InputProps={{ disableUnderline: true }}
              onChange={handleChange}
              className={classes.inputFieldRight}
            />
          </div>

          <div className={classes.formGroup}>
            <label>Prefectures</label>
            <TextField
              name="company"
              InputProps={{ disableUnderline: true }}
              onChange={handleChange}
              className={classes.inputFieldPrefectures}
            />
            <FormLabel className={classes.FormRightLevel}>
              service level
            </FormLabel>
            <TextField
              name="serviceLevel"
              InputProps={{ disableUnderline: true }}
              onChange={handleChange}
              className={classes.inputFieldRight}
            />
          </div>
      </form>
    </React.Fragment>
  );
};

export default BasicInfoAdd;
