import React from "react";
import { Box, Checkbox, FormControlLabel, FormLabel, makeStyles, Modal, Radio, RadioGroup, TextField, Typography } from "@material-ui/core";
import { formDataType } from "../Homepage";
import { ErrorType } from "../Homepage";
import SearchIcon from "@mui/icons-material/Search";
import { MoreHoriz} from "@mui/icons-material";
import MyInputfield from "../MyInputfield";

type DemoFormProps = {
  data: formDataType;
  setData: React.Dispatch<React.SetStateAction<formDataType>>;
  errors: ErrorType;
  setErrors: React.Dispatch<React.SetStateAction<ErrorType>>;
  handleChange: (event: React.ChangeEvent<HTMLInputElement>) => void;
};

const useStyles = makeStyles({
  form: {
    border: "1px solid black",
    padding: "5px 10px",
    margin: "10px",
  },
  formGroup: {
    display: "grid",
    gridTemplateColumns: "repeat(8, 1fr)",
    padding: "5px 0px",
    gap: "8px",
    alignItems: "center",
  },
  inputField: {
    border: "1px solid black",
    backgroundColor: "white",
    borderRadius: "50px",
    gridColumn: "2/4",
    height: "25px",
    marginBottom: "5px",
    "& .MuiFormHelperText-root": {
      marginTop: "-9px",
      marginLeft: "5px",
      fontSize: "12px",
    },
  },
  inputFieldLeftLarge: {
    border: "1px solid black",
    backgroundColor: "white",
    borderRadius: "50px",
    gridColumn: "2/5",
    height: "25px",
    marginBottom: "5px",
    "& .MuiFormHelperText-root": {
      marginTop: "-9px",
      marginLeft: "5px",
      fontSize: "12px",
    },
  },
  inputFieldRight: {
    border: "1px solid black",
    backgroundColor: "white",
    borderRadius: "50px",
    gridColumn: "6/7",
    height: "25px",
    marginBottom: "5px",
    "& .MuiFormHelperText-root": {
      marginTop: "-9px",
      marginLeft: "5px",
      fontSize: "12px",
    },
