import React from "react";
import { Checkbox, FormControlLabel, makeStyles, Radio, RadioGroup, TextField } from "@material-ui/core";
import { formDataType } from "../Homepage";
import { ErrorType } from "../Homepage";
import SearchIcon from "@mui/icons-material/Search";

type DemoFormProps = {
  data: formDataType;
  setData: React.Dispatch<React.SetStateAction<formDataType>>;
  errors: ErrorType;
  setErrors: React.Dispatch<React.SetStateAction<ErrorType>>;
};

const useStyles = makeStyles({
  form: {
    border: "1px solid black",
    margin: "10px",
  },
  formGroupAdd: {
    columnGap: "10px",
    display: "grid",
    gridTemplateColumns: "0.5fr 2fr 0.5fr 1.5fr",
    // gridTemplateColumns: "auto 2fr auto 1.5fr",
    padding: "10px",
  },
  formGroupCTitle: {
    gridTemplateColumns: "2fr 0.5fr 1fr 0.5fr",
    gap: "25px",
    display: "grid",
    padding: "5px",
  },
  formGroupRegion: {
    gap: "5px",
    display: "grid",
    gridTemplateColumns: "auto auto auto auto auto",
    // gridTemplateColumns: "0.3fr 1fr 0.3fr 1fr",
    padding: "10px",
  },
  formGroupCheck: {
    gap: "5px",
    display: "grid",
    gridTemplateColumns: "auto auto auto auto auto",
    // gridTemplateColumns: "0.3fr 1fr 0.3fr 1fr",
    padding: "10px",
  },
  inputField: {
    border: "1px solid black",
    borderRadius: "50px",
    width: "130px",
  },
  smallInputField: {
    border: "1px solid black",
    borderRadius: "50px",
    width: "90px",
  },
  titleField: {
    textAlign: "left",
    textSize: "16px",
  },
  errorMessage: {
    Color: "red",
    padding: "10px",
  },
});

const CustomerSIAdd: React.FC<DemoFormProps> = (props) => {
  const classes = useStyles();

