import React from "react";
import { FormLabel, makeStyles, TextField } from "@material-ui/core";
import { formDataType, ErrorType } from "../Homepage";

type DemoFormProps = {
  data: formDataType;
  setData: React.Dispatch<React.SetStateAction<formDataType>>;
  errors: ErrorType;
  setErrors: React.Dispatch<React.SetStateAction<ErrorType>>;
};

const useStyles = makeStyles({
  form: {
    backgroundColor: "#4BFC7E",
    margin: "0px 10px",
  },
  formGroup: {
    display: "grid",
    gridTemplateColumns: "repeat(8, 1fr)",
    padding: "5px",
  },
  FormLeftLevel: {
    gridColumn: "1/2",
    color: "black",
  },
  inputField: {
    border: "1px solid black",
    backgroundColor: "white",
    borderRadius: "50px",
    gridColumn: "2/8",
  },
  inputFieldRemarks: {
    border: "1px solid black",
    backgroundColor: "white",
    borderRadius: "10px",
    gridColumn: "2/8",
    height: "50px",
  },
  errorMessage: {
    Color: "red",
    padding: "10px",
  },
});

const EmailInfoAdd: React.FC<DemoFormProps> = (props) => {
  const classes = useStyles();
  const emailRegex =
    /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;

  // ============================== Methods =========================

  /** 
       This Method for get all change on the Input Field
      */
  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = event.target;

    props.setData((prev) => {
      return {
        ...prev,
        [name]: value,
      };
    });

    props.setErrors((prev) => ({
      ...prev,
      [name]: "",
    }));
  };

  return (
    <React.Fragment>
      <form className={classes.form}>
        <div className={classes.formGroup}>
          <FormLabel className={classes.FormLeftLevel}>Email 1</FormLabel>
          <TextField
            name="email1"
            className={classes.inputField}
            value={props.data.email1}
            required={true}
            InputProps={{ disableUnderline: true }}
            helperText={props.errors.email1}
            error={Boolean(props.errors.email1)}
            onChange={(event: React.ChangeEvent<HTMLInputElement>) => {
              if (event.target.value.match(emailRegex)) {
                handleChange(event);
              }
            }}
          />
        </div>
        <div className={classes.formGroup}>
          <FormLabel className={classes.FormLeftLevel}>Email 2</FormLabel>
          <TextField
            required={true}
            value={props.data.email2}
            name="email2"
            className={classes.inputField}
            InputProps={{ disableUnderline: true }}
            helperText={props.errors.email2}
            error={Boolean(props.errors.email2)}
            onChange={(event: React.ChangeEvent<HTMLInputElement>) => {
              if (event.target.value.match(emailRegex)) {
                handleChange(event);
              }
            }}
          />
        </div>
        <div className={classes.formGroup}>
          <FormLabel className={classes.FormLeftLevel}>Email 3</FormLabel>
          <TextField
            name="email3"
            className={classes.inputField}
            InputProps={{ disableUnderline: true }}
            helperText={props.errors.email3}
            error={Boolean(props.errors.email3)}
            onChange={(event: React.ChangeEvent<HTMLInputElement>) => {
              if (event.target.value.match(emailRegex)) {
                handleChange(event);
              }
            }}
          />
        </div>
        <div className={classes.formGroup}>
          <FormLabel className={classes.FormLeftLevel}>Remarks</FormLabel>
          {/* <TextareaAutosize
            aria-label="minimum height"
            minRows={3}
            className={classes.inputFieldRemarks}
          /> */}
          <TextField
            name="remarks"
            className={classes.inputFieldRemarks}
            InputProps={{ disableUnderline: true }}
            onChange={handleChange}
            helperText={props.errors.remarks}
            error={Boolean(props.errors.remarks)}
          />
        </div>
      </form>
    </React.Fragment>
  );
};

export default EmailInfoAdd;
