import React from "react";
import { Checkbox, FormControlLabel, FormLabel, makeStyles, Radio, TextField } from "@material-ui/core";
import { formDataType } from "../Homepage";
import { ErrorType } from "../Homepage";
import SearchIcon from "@mui/icons-material/Search";
import { MoreHoriz, Percent } from "@mui/icons-material";

type DemoFormProps = {
  data: formDataType;
  setData: React.Dispatch<React.SetStateAction<formDataType>>;
  errors: ErrorType;
  setErrors: React.Dispatch<React.SetStateAction<ErrorType>>;
};

const useStyles = makeStyles({
  form: {
    border: "1px solid black",
    margin: "10px",
  },
  formGroup: {
    display: "grid",
    gridTemplateColumns: "repeat(8, 1fr)",
    padding: "5px",
  },
  inputField: {
    border: "1px solid black",
    backgroundColor: "white",
    borderRadius: "50px",
    gridColumn: "2/3",
  },
  inputFieldLeftLarge: {
    border: "1px solid black",
    backgroundColor: "white",
    borderRadius: "50px",
    gridColumn: "2/4",
  },
  inputFieldRight: {
    border: "1px solid black",
    backgroundColor: "white",
    borderRadius: "50px",
    gridColumn: "6/7",
  },
  inputFieldRightLarge: {
    border: "1px solid black",
    backgroundColor: "white",
    borderRadius: "50px",
    gridColumn: "6/8",
  },
  FormLeftLevel: {
    gridColumn: "1/2",
    padding: "0px 5px",
    color: "black",
  },
  FormTitleLevel: {
    gridColumn: "1/4",
    padding: "0px 5px",
    color: "black",
  },
  FormRightLevel: {
    gridColumn: "5/6",
    padding: "0px 5px",
    color: "black",
  },
  FormSelectButton1: {
    gridColumn: "6/7",
    color: "black",
  },
  FormSelectButton2: {
    gridColumn: "7/8",
