import React from "react";
import {
  FormLabel,
  makeStyles,
  TextField,
} from "@material-ui/core";
import { formDataType, ErrorType } from "../Homepage";
import MyInputfield from "../MyInputfield";

type DemoFormProps = {
  data: formDataType;
  setData: React.Dispatch<React.SetStateAction<formDataType>>;
  errors: ErrorType;
  setErrors: React.Dispatch<React.SetStateAction<ErrorType>>;
};

const useStyles = makeStyles({
  form: {
    border: "1px solid black",
    margin: "0px 10px",
  },
  formGroup: {
    display: "grid",
    gridTemplateColumns: "repeat(8, 1fr)",
    padding: "5px",
  },
  FormLeftLevel: {
    gridColumn: "1/3",
    color: "black",
    paddingTop: "10px",
    "&.MuiFormLabel-root": {
      fontSize: "12px",
    },
  },
  inputField: {
    border: "1px solid black",
    borderRadius: "50px",
    gridColumn: "3/5",
  },
  errorMessage: {
    Color: "red",
    padding: "10px",
  },
});

const BlackInformationAdd: React.FC<DemoFormProps> = (props) => {
  const classes = useStyles();

  // ============================== Methods =========================

  /** 
       This Method for get all change on the Input Field
      */
  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = event.target;

    props.setData((prev) => {
      return {
        ...prev,
        [name]: value,
      };
    });

    props.setErrors((prev) => ({
      ...prev,
      [name]: "",
    }));
  };

  return (
    <React.Fragment>
      <form className={classes.form}>
        <div className={classes.formGroup}>
          {/* <FormLabel className={classes.FormLeftLevel}>
            black information
          </FormLabel>
          <TextField
            name="blackInformation"
            className={classes.inputField}
            InputProps={{ disableUnderline: true }}
            onChange={handleChange}
            helperText={props.errors.blackInformation}
            error={Boolean(props.errors.blackInformation)}
          /> */}
          <MyInputfield
            labelText="black information"
            labelClass={`${classes.FormLeftLevel}`}
            name="firstName"
            value={props.data.firstName}
            required={true}
            textFieldClass="inputField"
            handleChange={props.handleChange}
            helperText={props.errors.firstName}
            error={Boolean(props.errors.firstName)}
          />
        </div>

        <div className={classes.formGroup}>
          <FormLabel className={classes.FormLeftLevel}>
            black information note
          </FormLabel>
        </div>
      </form>
    </React.Fragment>
  );
};

export default BlackInformationAdd;
