import { FormLabel, makeStyles, TextField } from '@material-ui/core';
import React from 'react';

type MyInputfieldProps = {
  labelText: string;
  labelClass: string;
  name: string;
  value?: string;
  //   onChange: () => void,
  helperText: string;
  error: boolean;
  required: boolean;
  textFieldClass: string;
  handleChange: (event: React.ChangeEvent<HTMLInputElement>) => void;
};

const useStyles = makeStyles({
  LeftLevel: {
    gridColumn: "1/2",
    color: "black",
    "&.MuiFormLabel-root": {
      fontSize: "12px",
    },
    width: "100px",
  },
  RightLevel: {
    gridColumn: "6/7",
    color: "black",
    "&.MuiFormLabel-root": {
      fontSize: "12px",
    },
  },
  inputField: {
    border: "1px solid black",
    backgroundColor: "white",
    borderRadius: "50px",
    gridColumn: "2/4",
  },
  inputFieldLarge: {
    border: "1px solid black",
    backgroundColor: "white",
    borderRadius: "50px",
    gridColumn: "2/6",
  },
  inputFieldRight: {
    border: "1px solid black",
    backgroundColor: "white",
    borderRadius: "50px",
    gridColumn: "7/9",
  },
  errorMessage: {
    Color: "red",
    padding: "10px",
  },
});

const MyInputfield : React.FC<MyInputfieldProps> = (props) => {
    const { labelText, labelClass, name, value, required, error, helperText, textFieldClass, handleChange } =
      props;
  const classes = useStyles();

    return (
      <React.Fragment>
        {/* <FormLabel className={`classes.${labelClass}`}>{labelText}</FormLabel> */}
        {/* <FormLabel className={`${classes.LeftLevel} ${labelClass}`}> */}
        <FormLabel className={labelClass}>
          {labelText}
        </FormLabel>
        <TextField
          name={name}
          value={value}
          required={required}
          InputProps={{ disableUnderline: true}}
          className={textFieldClass}
          onChange={handleChange}
          helperText={helperText}
          error={error}
        />
      </React.Fragment>
    );
};

export default MyInputfield;
