import React from "react";
import { FormLabel, makeStyles, TextField } from "@material-ui/core";
import { formDataType, ErrorType } from "../Homepage";
import SearchIcon from "@mui/icons-material/Search";

type DemoFormProps = {
  data: formDataType;
  setData: React.Dispatch<React.SetStateAction<formDataType>>;
  errors: ErrorType;
  setErrors: React.Dispatch<React.SetStateAction<ErrorType>>;
};

const useStyles = makeStyles({
  form: {
    // display: "grid",
    // gridTemplateColumns: "repeat(4, 1fr)",
    // gridTemplateColumns: "auto auto",
    // gap: "50px",
    backgroundColor: "skyblue",
    margin: "0px 10px",
  },
  formGroup: {
    // gap: "20px",
    display: "grid",
    gridTemplateColumns: "repeat(8, 1fr)",
    // gridTemplateColumns: "0.5fr 2fr",
    padding: "10px 0px",
    // width: "200px",
  },
  formGroupName: {
    display: "grid",
    gridTemplateColumns: "repeat(8, 1fr)",
    // gridTemplateColumns: "0.5fr 1fr 0.5fr 2fr",
    padding: "10px 0px",
  },
  formGroupSearch: {
    // gap: "20px",
    display: "grid",
    gridTemplateColumns: "repeat(8, 1fr)",
    padding: "10px 0px",
  },
  formGroupService: {
    gap: "20px",
    display: "grid",
    gridTemplateColumns: "0.5fr 2fr 0.5fr 1.5fr",
    padding: "10px",
  },
  inputField: {
    border: "1px solid black",
    backgroundColor: "white",
    borderRadius: "50px",
    width: "200px",
  },
  inputField1: {
    border: "1px solid black",
    backgroundColor: "white",
    borderRadius: "50px",
    gridColumn: "2/4",
  },
  inputFieldRight: {
    border: "1px solid black",
    backgroundColor: "white",
    borderRadius: "50px",
    gridColumn: "7/9",
  },
  FormLeftLevel: {
    gridColumn: "1/2",
    color: "black",
  },
  FormTitleLevel: {
    gridColumn: "4/5",
    color: "black",
  },
  FormRightLevel: {
    gridColumn: "6/7",
    color: "black",
  },
  titleField: {
    border: "1px solid black",
    backgroundColor: "white",
    borderRadius: "50px",
    // width: "90px",
    gridColumn: "5/6",
  },
  prefecturesField: {
    border: "1px solid black",
    backgroundColor: "white",
    borderRadius: "50px",
    width: "90px",
  },
  form__back__button: {
    padding: "5px",
    color: "black",
    backgroundColor: "#27AE60",
    borderRadius: "5px",
    width: "100px",
    margin: "15px",
    marginLeft: "580px",
  },
  errorMessage: {
    Color: "red",
    padding: "10px",
  },
});

const BasicInfoAdd: React.FC<DemoFormProps> = (props) => {
  const classes = useStyles();
  const phoneNoRegex = "^[0-9-]+$|^$";

  // ============================== Methods =========================

  /** 
       This Method for get all change on the Input Field
      */
  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = event.target;

    props.setData((prev) => {
      return {
        ...prev,
        [name]: value,
      };
    });

    props.setErrors((prev) => ({
      ...prev,
      [name]: "",
    }));
  };

  return (
    <React.Fragment>
      <form>
        {/* <Link to={`/`}>
          <button className={classes.form__back__button}>Back</button>
        </Link> */}
        <div className={classes.form} id="BasicInfoAdd">
          <div className={classes.formGroup}>
            <FormLabel className={classes.FormLeftLevel}>
              phone number
            </FormLabel>
            <TextField
              name="phoneNo"
              value={props.data.phoneNo}
              required={true}
              InputProps={{ disableUnderline: true }}
              className={classes.inputField1}
              helperText={props.errors.phoneNo}
              error={Boolean(props.errors.phoneNo)}
              onChange={(event: React.ChangeEvent<HTMLInputElement>) => {
                if (event.target.value.match(phoneNoRegex)) {
                  handleChange(event);
                }
              }}
            />
          </div>

          <div className={classes.formGroupName}>
            <>
              <FormLabel className={classes.FormLeftLevel}>Your name</FormLabel>

              {/* <label className={classes.FormLeftLevel}>Your name</label> */}
              <TextField
                id="firstName"
                name="firstName"
                className={classes.inputField1}
                InputProps={{ disableUnderline: true }}
                value={props.data.firstName}
                required={true}
                onChange={handleChange}
                helperText={props.errors.firstName}
                error={Boolean(props.errors.firstName)}
              />
            </>
            <>
              <FormLabel className={classes.FormTitleLevel}>title</FormLabel>
              <TextField
                name="lastName"
                value={props.data.lastName}
                InputProps={{ disableUnderline: true }}
                required={true}
                onChange={handleChange}
                className={classes.titleField}
                helperText={props.errors.lastName}
                error={Boolean(props.errors.lastName)}
              />
            </>
          </div>

          <div className={classes.formGroup}>
            <FormLabel className={classes.FormLeftLevel}>Furigana</FormLabel>
            <TextField
              name="company"
              value={props.data.company}
              InputProps={{ disableUnderline: true }}
              onChange={handleChange}
              className={classes.inputField1}
              helperText={props.errors.company}
              error={Boolean(props.errors.company)}
            />
          </div>
          <div className={classes.formGroupSearch}>
            <FormLabel className={classes.FormLeftLevel}>Search</FormLabel>
            <TextField
              name="search"
              InputProps={{ disableUnderline: true }}
              onChange={handleChange}
              className={classes.inputField1}
            />
            <SearchIcon />
            <FormLabel className={classes.FormRightLevel}>sort code</FormLabel>
            {/* <label>sort code</label> */}
            <TextField
              name="sortCode"
              InputProps={{ disableUnderline: true }}
              onChange={handleChange}
              className={classes.inputFieldRight}
            />
          </div>

          <div className={classes.formGroupService}>
            <>
              <label>Prefectures</label>
              <TextField
                name="company"
                value={props.data.company}
                InputProps={{ disableUnderline: true }}
                onChange={handleChange}
                className={classes.prefecturesField}
              />
            </>
            <>
              {/* <label>service level</label> */}
              <TextField
                name="sortCode"
                InputProps={{ disableUnderline: true }}
                onChange={handleChange}
                className={classes.inputField}
              />
            </>
          </div>
        </div>
      </form>
    </React.Fragment>
  );
};

export default BasicInfoAdd;
